import{gsap}from"gsap";import{ScrollTrigger}from"gsap/ScrollTrigger";gsap.registerPlugin(ScrollTrigger);const section3TL=gsap.timeline();section3TL.from("#section-3",{duration:.5,alpha:0});export function section3Animation(){ScrollTrigger.create({markers:!0,animation:section3TL,toggleActions:"play none none none",trigger:"#section-3",start:"top, 50%",end:"bottom 50%"})}