import{gsap}from"gsap";import{ScrollTrigger}from"gsap/ScrollTrigger";gsap.registerPlugin(ScrollTrigger);const section4TL=gsap.timeline();section4TL.from("#section-4 aside",{duration:.5,alpha:0,x:-300,rotate:-90});export function section4Animation(){ScrollTrigger.create({markers:!0,animation:section4TL,toggleActions:"play none none none",trigger:"#section-4",start:"top, 50%",end:"bottom 50%"})}