import{gsap}from"gsap";import{ScrollTrigger}from"gsap/ScrollTrigger";gsap.registerPlugin(ScrollTrigger);const section5TL=gsap.timeline();section5TL.from("#section-5 article img",{duration:.5,alpha:0,x:-300,rotate:-90});export function section5Animation(){ScrollTrigger.create({markers:!0,animation:section5TL,toggleActions:"play none none none",trigger:"#section-5",start:"top, 50%",end:"bottom 50%"})}