import{gsap}from"gsap";import{ScrollTrigger}from"gsap/ScrollTrigger";gsap.registerPlugin(ScrollTrigger);const section4TL=gsap.timeline();section4TL.from("#section-6 article img",{duration:.5,alpha:0,x:-300,rotate:-90}).from("#section-6 article h3",{duration:.5,alpha:0,x:300,rotate:90},"second").from("#section-6 article p",{duration:.5,alpha:0},"second").from("#section-6 article h4",{duration:.5,alpha:0,y:400});export function section6Animation(){ScrollTrigger.create({markers:!0,animation:section6TL,toggleActions:"play none none none",trigger:"#section-6",start:"top, 50%",end:"bottom 50%"})}